targets.expr:
avg by (owner_name,modelName) (
  label_replace(
    DCGM_FI_DEV_GPU_UTIL{pod!="", namespace="crater-workspace"},
    "pod", "$1", "pod", "(.*)"
  )
  * on (pod) group_left(owner_name) kube_pod_owner{namespace="crater-workspace", owner_is_controller="true", owner_kind="Job"}
)

avg by (owner_name) (
  (
    label_replace(
      DCGM_FI_DEV_FB_USED{pod!="", namespace="crater-workspace"},
      "pod", "$1", "pod", "(.*)"
    )
    /
    (
      label_replace(
        DCGM_FI_DEV_FB_USED{pod!="", namespace="crater-workspace"},
        "pod", "$1", "pod", "(.*)"
      )
      +
      label_replace(
        DCGM_FI_DEV_FB_FREE{pod!="", namespace="crater-workspace"},
        "pod", "$1", "pod", "(.*)"
      )
    )
  ) * 100
  * on (pod) group_left(owner_name) kube_pod_owner{namespace="crater-workspace", owner_is_controller="true", owner_kind="Job"}
)
